/** words with friends styles styles **/

.scrabble-container .ui.table tbody tr td.selectable.played-tile,
.scrabble-container .ui.table tbody tr td.selectable.played-tile:hover {
  background: #FCE356 !important;
}
.tile-bg-color {
  background: #f9fafb;
}

/* http://www.colourlovers.com/palette/2571794/Words_with_Friends */

.tw,
.ui.table tbody tr td.selectable.tw:hover {
  background-color: #E19235!important; /* @orange */
}
.tw div:empty:before {
  content: 'TW';
  color: #fff;
}
.dw,
.ui.table tbody tr td.selectable.dw:hover {
  background-color: #B2483B!important; /* @red */
}
.dw div:empty:before {
  content: 'DW';
  color: #fff;
}
.tl,
.ui.table tbody tr td.selectable.tl:hover {
  background-color: #66A453!important; /* @green */
}
.tl div:empty:before {
  content: 'TL';
  color: #fff;
}
.dl,
.ui.table tbody tr td.selectable.dl:hover {
  background-color: #0082C8!important; /* @blue */
}
.dl div:empty:before {
  content: 'DL';
  color: #fff;
}

.on-board {
  /* doesn't really need a style since its obvious its on there */
}
div.blank-tile {
  display: inline;
  border-bottom: 2px solid black!important;
  width: 20px!important;
  margin-left: 5px!important;
  display: block!important;
  /*height: 30px;*/
}

.scrabble-container .ui.table tbody tr td.selectable.highlight-word-location {
  /* make the same as .tile-background-color */

  background: #f9fafb !important;
}

.scrabble-container .ui.table tbody tr td.selectable.highlight-word-location span {
    color: rgba(0,0,0,.95);
    border-radius: .28571429rem;
    background: #fff!important;
    width: 100%;
    vertical-align: top;
    box-shadow: 0 0 0 0 rgba(34,36,38,.35) inset;
    margin: 0;
    outline: 0;
    transition: color .1s ease,border-color .1s ease;
    padding: 3px 0;
    border: 1px solid #85b7d9;
    box-shadow: inset 0px 0px 10px #d8d8d8;
    height: 26px;
    display: block;
    height: 100%;
}
.scrabble-container .ui.table tbody tr td.selectable.played-tile.highlight-word-location.on-board  {
  background:     background: #FCE356!important;
}
.scrabble-container .ui.table tbody tr td.selectable.played-tile.highlight-word-location.on-board span {
  background: #FCE356 !important;
  border: 1px solid rgba(34,36,38,.1);
  color: inherit;
  font-weight: inherit;
  border-radius: inherit;
  box-shadow: none;
}

/*.scrabble-container .ui.table tbody tr td.selectable.played-tile*/

.section-toggle-game {
  /*float: right;*/
  height: auto;
  width: 180px;
  /*text-align: right;*/
  margin-right: 25px;
}


.scrabble-container {
  /* Set fixed min width so table doesn't get crumpled - can fix for mobile later */
  min-width: 756px;
  padding-left: 0;
  font-size: 1em;
}

.scrabble-container .ui.input input[type=text] {
  padding: 3px 0;
  text-align: center;
  box-shadow: inset 0px 0px 10px #d8d8d8;
}

.ui.icon.button.info-popover {
  margin-left: 8px;
  font-size: 1.1rem;
  border-radius: .2rem;
  padding: 2px 1px;
}

.big-underscore {
  font-size: 2rem;
  position: relative;
  top: -3px;
  margin-left: 3px;
  margin-right: 3px;
  line-height: 1px;
}

.ui.label {
  background-color: #fff;
}

.ui.vertical.menu.scrollable {
  display: block;
  height: 389px;
  width: 20.6rem;
  overflow-y: scroll;
  scroll-behavior: smooth;
}

.ui.vertical.scrollable.menu .item>.label {
  cursor: pointer;
  background: #333;
  color: #fff;
  padding: 4px 4px;
  margin-top: -3px;
}

.ui.vertical.scrollable.menu .item>.label:hover {
  background: #000;
  color: #fff;
}

.hover-word {
  background: green;
}

.btn-get-word {
  width: 110px;
}

.ui.input.rack {
  width: 160px;
  height: 36px;
}

.ui.input.rack input {
  letter-spacing: 5px;
}

.ui.input.rack input::placeholder {
  letter-spacing: 1px;
}

.ui .selectable {
  cursor: pointer;
}

.ui.table {
  table-layout: fixed;
  width: 450px; /* 40 * 15 */
}
.ui.table td {
  height: 30px;
  width: 30px;
  padding: 0;
}

.ui.table tr.highlight-word-location,
.ui.table td.highlight-word-location {
  box-shadow: 0px 0px 0px red inset;
}

.ui.button {
  background: #333;
  color: #fff;
}
.ui.button:hover {
  background: #000;
  color: #fff;
}

.ui.button.btn-tile-submit {
  position: absolute;
  top: 0;
  left: 30px;
  height: 25px;
  width: 25px;
  padding: 0;
}
.ui.button.btn-tile-submit.down-arrow,
.ui.button.btn-tile-submit.move-down {
  top: 26px;
  left: 2px;
}
